angular.module('GrainBilld')
.controller('loginCtrl', ["$scope", "loginService", "$state", function($scope, loginService, $state) {
    $scope.showLogin    = true;
    $scope.showRegister = false;
    $scope.register = function(firstName, lastName, email, password) {
        loginService.register(firstName, lastName, email, password).then(function(resp) {
            $scope.showSuccess = true;
            $state.go('home');
        }).catch(function(err) {
            $scope.showError = true;
            $state.go('login');
        });
    };
    $scope.login = function(email, password) {
        loginService.login(email, password).then(function(resp) {
            $scope.showSuccess = true;
            $state.go('home');
            $scope.email = '';
            $scope.password = '';
            $scope.showError = false;
        }).catch(function(err) {
            $scope.showError = true;
            console.log(err);
            $state.go('login');
        });
    };

    $scope.getCurrentUser = function(userId) {
        loginService.getCurrentUser().then(function(resp) {
            if(currUser === user);
            $state.go('home');
        }).catch(function(err) {
            console.log(err);
        });
    };
}]);

angular.module('GrainBilld')
.service('loginService', ["$http", "$q", function($http, $q) {
    var currUser = null;
    this.currentUser = function(){ return currUser; };

    this.getCurrentUser = function() {
        var dfd = $q.defer();
        $http({
            method: 'GET',
            url:    'http://localhost:8080/api/user/'
        }).success(function(user) {
            currUser = user;
            dfd.resolve(user);
        }).error(function(err) {
            dfd.reject(err);
        });
        return dfd.promise;
    };

    this.register = function(firstName, lastName, email, password) {
        var dfd = $q.defer();
        $http({
            method: 'POST',
            url:    'http://localhost:8080/auth/local/signup',
            data: {
                firstName:  firstName,
                lastName:   lastName,
                email:      email,
                password:   password
            }
        }).then(function(resp) {
            dfd.resolve(resp.data);
        }, function(err) {
            dfd.reject(err);
        });
        return dfd.promise;
    };

    this.login = function(email, password) {
        var dfd = $q.defer();
        $http({
            method: 'POST',
            url: 'http://localhost:8080/auth/local/login',
            data: {
                email:      email,
                password:   password
            }
        }).then(function(resp) {
            currUser = resp.data;
            dfd.resolve(resp.data);
        }, function(err) {
            dfd.reject(err);
        });
        return dfd.promise;
    };
}]);

